async function waitForCompletion(jobId: string) {
  const completedStatuses = await pipe(
    dropWhile((status: JobStatus) => status.status !== 'completed'),
    toArray
  )(pollJobStatus(jobId));

  return completedStatuses[0]; // First completed status
}

